.github-projects {
  grid-column: 1/-1;
  margin-bottom: 8rem;
}

.github-projects__loading {
  text-align: center;
  padding: 4rem 0;
  font-size: var(--font-size-20);
  color: var(--color-text);
  opacity: 0.7;
}

.github-projects__grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(32rem, 1fr));
  gap: 3rem;
  margin-top: 3rem;
}

.github-project-card {
  background: var(--color-background);
  border: 2px solid var(--color-grey);
  border-radius: 1.5rem;
  padding: 3rem;
  transition: all var(--transition-timing);
  opacity: 0;
  transform: translateY(2rem);
  transition: opacity 0.6s ease, transform 0.6s ease, border-color 0.3s ease,
    box-shadow 0.3s ease;

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }

  &:hover {
    border-color: var(--color-green);
    transform: translateY(-0.5rem);
    box-shadow: 0 1rem 3rem rgba(3, 166, 150, 0.2);
  }
}

.github-project-card__header {
  margin-bottom: 1.5rem;
}

.github-project-card__title {
  font-size: var(--font-size-24);
  font-weight: var(--font-weight-bold);
  margin-bottom: 1rem;
  color: var(--color-text);
  word-break: break-word;
}

.github-project-card__meta {
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: 1rem;
}

.github-project-card__language {
  padding: 0.4rem 1rem;
  border-radius: 1.5rem;
  font-size: var(--font-size-15);
  font-weight: var(--font-weight-medium);
}

.github-project-card__stats {
  display: flex;
  gap: 1.5rem;
  font-size: var(--font-size-15);
  color: var(--color-text);
  opacity: 0.7;
}

.github-project-card__description {
  font-size: var(--font-size-18);
  line-height: 1.7;
  color: var(--color-text);
  opacity: 0.8;
  margin-bottom: 2rem;
  min-height: 5rem;
}

.github-project-card__footer {
  display: flex;
  gap: 2rem;
  flex-wrap: wrap;
  margin-top: 2rem;
  padding-top: 2rem;
  border-top: 1px solid var(--color-grey);
}

.github-project-card__link {
  font-size: var(--font-size-18);
  font-weight: var(--font-weight-medium);
  color: var(--color-green);
  text-decoration: none;
  transition: all var(--transition-timing);
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;

  &:hover {
    color: var(--color-dark-green);
    transform: translateX(0.3rem);
  }

  &--demo {
    color: var(--color-darker-blue);

    &:hover {
      color: var(--color-blue);
    }
  }
}

@media (max-width: 800px) {
  .github-projects__grid {
    grid-template-columns: 1fr;
    gap: 2rem;
  }

  .github-project-card {
    padding: 2rem;
  }
}
